const DEFAULT_RUNTIME_CONTEXT = {};
export const toProductContext = (context, runtimeContext) => {
    const { principal, installContext } = runtimeContext || DEFAULT_RUNTIME_CONTEXT;
    const { cloudId, contentId, localId, spaceKey, isConfig, platformContext, } = context;
    return {
        accountId: principal && principal.accountId,
        cloudId,
        contentId,
        localId,
        spaceKey,
        installContext,
        platformContext,
        isConfig,
    };
};
