export const isInitializeEffect = (effect) => {
    return effect.type === 'initialize';
};
export const isEventEffect = (effect) => {
    return effect.type === 'event';
};
export const isActionEffect = (effect) => {
    return effect.type === 'action';
};
export const isRenderEffect = (effect) => {
    return effect.type === 'render';
};
export function isBackendEffect(effect) {
    return (isInitializeEffect(effect) ||
        isActionEffect(effect) ||
        isEventEffect(effect));
}
